mapper.insert:
INSERT INTO origin_data (
                          data_seq
                        , location
                        , location_code
                        , pm25
                        , pm10
                        , info_date
                        )
VALUES                  (
                          data_seq.NEXTVAL
                        , #{location}
                        , #{location_code}
                        , #{pm25}
                        , #{pm10}
                        , CASE

                            WHEN SUBSTR(#{info_date}, -2) = '24' THEN TO_CHAR(TO_DATE(REPLACE(#{info_date},' 24',' 00'), 'YYYY-MM-DD HH24') + 1, 'YYYY-MM-DD HH24')

                            ELSE #{info_date}
                          END
                        )
